function saveRepository(n){$(n.currentTarget).addClass("loading");var t="#"+$(n.currentTarget).data("id"),i=createSaveRepositoryModel(t);$.ajax({url:"Repositories/SaveRepository",type:"POST",async:!0,contentType:"application/json",dataType:"json",data:JSON.stringify(i),success:function(t){Boolean(t)?saveRepositorySucceeded(n):saveRepositoryFailed()},error:function(){saveRepositoryFailed()}})}function createSaveRepositoryModel(n){var t={};return $(n).each(function(){var n=$(this).find("input").serializeArray();$.each(n,function(n,i){t[i.name]=i.value})}),console.log(t),t}function saveRepositorySucceeded(n){$(n.currentTarget).removeClass("loading primary").addClass("basic");$(n.currentTarget).find("[data-button='icon']").removeClass("save").addClass("checkmark");$(n.currentTarget).find("[data-button='text']").html("Saved")}function saveRepositoryFailed(){alert("Oops...")}$(".ui.dropdown").dropdown();$(".save-repository-button").on("click",saveRepository);